    def test_decrypt_empty_value(self):
        password = 'a'
        decrypted_value = ''
        encrypted_value = 'AwHZ12GNPHAubGMes/O35F86hel8B6ZuqC9Lc1ZDsbnEHdPIcyBemqzKXSLX5lVlEW7x2ZiuQe0zZCZKXLCFEIxSLNy/uD0F1/z/5C0vBXO9Ew=='
